mapper.select:
	SELECT * FROM (
		SELECT Tb.*, rownum rNum FROM(
			SELECT * FROM photoboard

			WHERE ${searchField} LIKE '%'||#{searchKeyword} ||'%'

			ORDER BY idx DESC
		) Tb
	)
 	WHERE rNum>=#{start} AND rNum<=#{end}

	SELECT * FROM (
		SELECT Tb.*, rownum rNum FROM(
			SELECT * FROM photoboard

			WHERE ${searchField} LIKE '%'||#{searchKeyword} ||'%'

			ORDER BY idx DESC
		) Tb
	)
 	WHERE rNum>=#{start} AND rNum<=#{end} AND  email=#{email}
